<!-- Página de filtro -->
<div class="row">
  <form class="" action="index.html" method="post">
    <!-- Filtro por tipo de venda -->
    <div class="input-field col s6 m4">
      <select class="" name="">
        <option value="aluga">Aluga</option>
        <option value="vende">Vende</option>
      </select>
      <label>Status</label>
    </div>
    <!-- Filtro de tipo de casa -->
    <div class="input-field col s6 m4">
      <select class="" name="">
        <option value="1">Alvenaria</option>
        <option value="2">Apartamento</option>
        <option value="3">Duplex</option>
      </select>
      <label>Tipo de imóvel</label>
    </div>
    <!-- Filtro por cidade -->
    <div class="input-field col s6 m4">
      <select class="" name="">
        <option value="1">Sao Paulo</option>
        <option value="2">Santo André</option>
        <option value="2">São Caetano</option>
      </select>
      <label>Cidade</label>
    </div>
    <!-- Filtro por quantidade de quartos -->
    <div class="input-field col s6 m3">
      <select class="" name="">
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        <option value="4">Mais</option>
      </select>
      <label>Dormitórios</label>
    </div>
    <!-- Filtro por valores -->
    <div class="input-field col s12 m4">
      <select class="" name="">
        <option value="1">Até R$500,00</option>
      </select>
      <label>Valor</label>
    </div>
    <!-- Filtro por bairro-->
    <div class="input-field col s12 m3">
      <input type="text" class="validate" name="bairro">
      <label>Bairro</label>
    </div>
    <!-- Botão de filtro -->
    <div class="input-field col s12 m2">
      <button class="btn deep-orange darken-1 right">Filtrar</button>
    </div>
  </form>
</div>
